

console.log(`---------------------------`);
// Log numbers from 5 to 15
for (let index = 5; index <=15; index++) {
    console.log(index);
}

console.log("----------------------------")
// decrementing from 50 to 40
for (let index = 50; index >=40 ; index=index-1) {
    console.log(index);  
}

console.log("----------------------------")
// First 15 odd numbers
for (let index = 1; index <15 ; index=index+2) {
    console.log(index); 
}

console.log("----------------------------")
// First 10 even number
for (let index = 0; index <20 ; index=index+2) {
    console.log(index); 
}

console.log("----------------------------")
// 5 table
for (let index = 5; index <=50 ; index=index+5) {
    console.log(index);  
}

console.log("----------------------------")
// 10 table
for (let index = 10; index <=100 ; index=index+10) {
    console.log(index);  
}

console.log("----------------------------")
// 10 table in reverse order
for (let index = 100; index >=10 ; index=index-10) {
    console.log(index);  
}