

console.log(`---------------------`);
// Numbers from 5 to 15
var i = 5;
while (i<=15) {
    console.log(i);
    i++;
}

console.log(`---------------------`);
// Decrementing by 1 from 50 to 40 
var i = 50;
while (i>=40) {
    console.log(i);
    i--;
}

console.log(`---------------------`);
// first 15 odd numbers 
var i = 1;
while (i<=29) {
    console.log(i);
    i+=2;
}

console.log(`---------------------`);
// first 10 even numbers 
var i = 0;
while (i<=18) {
    console.log(i);
    i+=2;
}

console.log(`---------------------`);
// print table of 5
var i = 5;
while (i<=50) {
    console.log(i);
    i+=5;
}

console.log(`---------------------`);
// print table of 10
var i = 10;
while (i<=100) {
    console.log(i);
    i+=10;
}

console.log(`---------------------`);
// print table of 10 in Reverse order
var i = 100 ;
while (i>=10) {
    console.log(i);
    i-=10;
}
