//Assignment:  WAP to find the factorial of numbers: 5, 9, 11, 7

// 5! = 1 * 2 * 3 * 4 * 5 * 6 * 7 * 8 * 9

let result = 1; // 3 
for (let index = 1; index <= 5; index++) {
    result = result * index;
}
console.log(`Factorial of 5 is ${result}`);


